import React from "react";
import { Card, Container, Row, Button, Col } from "react-bootstrap";
import Ruppee from "../../../headers/icons/rupee-sign-solid.svg";
import "./productItem.css";


export default function ProductItem(props) {
  const product = props.product;
  const cardImage = {
    objectFit: "cover",
    borderRadius: 10,
    width: "100%",
    height: "30vh",
  };
  return (
    <Col xl={3} lg={4} md={6}>
      <Card style={{ width: "18rem", margin: "2%" }}>
        <Card.Img
          src={product.images.url}
          style={cardImage}
        ></Card.Img>
        <Card.Body>
          <Card.Title className="productTitle">{product.title}</Card.Title>
          <Card.Text>{product.description}</Card.Text>
          <Card.Text><span style={{fontWeight: "bold"}}>Price:  </span> <span><img src={Ruppee}></img></span>{product.price}</Card.Text>
          <Button variant="primary">Buy Now</Button>
        </Card.Body>
      </Card>
    </Col>
  );
}
