import React, { useState } from "react";
import { Button, ButtonGroup, Col, Container, Form, Row } from "react-bootstrap";

export default function Login() {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  const styles = {
    containerStyle: {
      width: "40%",
      border: "1px solid",
      padding: "20px",
      margin: "5% auto 0",
    },
    formStyle: {
      margin: "0 auto !important",
    },
    primaryBtnStyle: {
        marginTop: "30px"
    }
  };
  return (
    <Container style={styles.containerStyle}>
      <Form style={styles.formStyle}>
        <Form.Group as={Row} className="mb-3">
          {/* <Form.Label column sm="2">Email Address</Form.Label> */}
          <Col sm="12">
            <Form.Control type="email" placeholder="Enter email" />
          </Col>
        </Form.Group>
        <Form.Group as={Row} className="mb-3">
          {/* <Form.Label column sm="2">Password</Form.Label> */}
          <Col sm="8">
            <Form.Control type="password" placeholder="Enter password" />
          </Col>
        </Form.Group>
        <Row>
        <Button variant="primary" style={styles.primaryBtnStyle}>Login Now</Button>
        <Button variant="link" href="/register">New user? Sign Up</Button>
        </Row>

        {/* <Form.Group as={Row} className="mb-3"> */}

        {/* </Form.Group> */}
        {/* <input type="email" name="email" required placeholder="Email" value = {user.email} />
                <input type="password" name="password" required placeholder="Password" value = {user.password} /> */}
      </Form>
    </Container>
  );
}
