import axios from "axios";
import React, { useState } from "react";
import { Button, ButtonGroup, Col, Container, Form, Row } from "react-bootstrap";

export default function Login() {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  const styles = {
    containerStyle: {
      width: "400px",
      border: "1px solid",
      padding: "20px",
      margin: "5% auto 0",
      textAlign: "center"
    },
    formStyle: {
      margin: "0 auto !important",
    },
    primaryBtnStyle: {
        margin: "30px auto 0",
        width: "50%",
    },
    colStyle: {
        margin: "0 auto",
    }
  };

  const onChangeInput = (event) => {
      const {name, value} = event.target;
      setUser({...user, [name]:value });
  }

  const loginHandle = async (event) => {
      event.preventDefault();
      try {
          await axios.post('/user/login', {...user})
          localStorage.setItem('firstLogin', true);

          window.location.href ='/'

      } catch (err) {
          alert(err.response.data.msg)
      }

  }
  return (
    <Container style={styles.containerStyle}>
      <Form style={styles.formStyle} onSubmit={loginHandle}>
        <Form.Group as={Row} className="mb-3">
          {/* <Form.Label column sm="2">Email Address</Form.Label> */}
          <Col sm="11" style={styles.colStyle}>
            <Form.Control type="email" name= "email" placeholder="Enter email" value={user.email || ""} onChange={onChangeInput}/>
          </Col>
        </Form.Group>
        <Form.Group as={Row} className="mb-3">
          {/* <Form.Label column sm="2">Password</Form.Label> */}
          <Col sm="11" style={styles.colStyle}>
            <Form.Control type="password" name="password" placeholder="Enter password" value={user.password || ""} onChange={onChangeInput} />
          </Col>
        </Form.Group>
        <Row>
        <Button variant="primary" type="submit" style={styles.primaryBtnStyle}>Login Now</Button><br></br>
        <Button variant="link" href="/register">New user? Sign Up</Button>
        </Row>

      </Form>
    </Container>
  );
}
